code.installcode:
if (!file_exists(DIR . '/dbtech/credits/credits_installer.php'))
{
	print_dots_stop();
	print_cp_message('In order to upgrade or install vBCredits, you must upload credits_installer.php to the plugins folder within your forum directory!');
}

$doinstall = true;
require_once(DIR . '/dbtech/credits/credits_installer.php');

